import React,{useState,useEffect} from 'react'
import {Link , useLocation, useHistory, useNavigate} from 'react-router-dom'
import {Form,Button,Row, Col} from 'react-bootstrap'
import {useDispatch,useSelector} from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import {register} from '../actions/userAction'
import FormContainer from '../components/FormContainer'

const RegisterScreen = () => {
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setconfirmPassword] = useState('')
  const [message, setMessage] = useState('')
  const location = useLocation();
  const history = useNavigate();


  const redirect = location.search ? location.search.split('=')[1] :"/";

  const dispacth = useDispatch();
  const registerUser = useSelector(state => state.registerUser)
  const {Loading,error,userInfo} = registerUser

useEffect(() => {
    if(userInfo){history(redirect)}
},[history,userInfo,redirect])
  const submitHandler =(e) => {
      e.preventDefault()
      dispacth(register(email,password))

  }
  return (
    <>
        <FormContainer>
          <h1>Register</h1>
          {error && <Message varient="danger">{error}</Message>}
          {Loading && <Loader />}
          {message && <Message varinat='danger'>{setMessage}</Message>}
          <Form onSubmit={submitHandler}>
              <Form.Group controlId="name">
                  <Form.Label>Name</Form.Label>
                  <Form.Control type="text" placeholder="enter name" value={name}
                  onChange={(e) => setName(e.target.value)}></Form.Control>
              </Form.Group>
              <Form.Group controlId="email">
                  <Form.Label>Email Address</Form.Label>
                  <Form.Control type="email" placeholder="enter email" value={email}
                  onChange={(e) => setEmail(e.target.value)}></Form.Control>
              </Form.Group>
              <Form.Group controlId="password">
                  <Form.Label>Password</Form.Label>
                  <Form.Control type="password" placeholder="enter password" value={password}
                  onChange={(e) => setPassword(e.target.value)}></Form.Control>
              </Form.Group>
              <Form.Group controlId="confirmPassword">
                  <Form.Label>Confirm Password</Form.Label>
                  <Form.Control type="password" placeholder="re-enter password" value={confirmPassword}
                  onChange={(e) => setconfirmPassword(e.target.value)}></Form.Control>
              </Form.Group>
              <Button type="submit" variant='primary'>SIGN IN</Button>
          </Form>
          <Row>
              <Col>
                Have an Account ? 
                <Link to={redirect ? `login?redirect=${redirect}` : '/login'}>Register</Link>
              </Col>
          </Row>
          </FormContainer>
    </>
  )
}

export default RegisterScreen
