import React,{useEffect, useState} from 'react'
import {Button, Col, Row , ListGroup, Image, Card, ListGroupItem} from 'react-bootstrap'
import { ORDER_PAY_RESET } from '../constants/orderConstants'
import {PayPalButton} from 'react-paypal-button-v2'
import {Link, useNavigate, useParams} from 'react-router-dom'
import {useDispatch, useSelector} from 'react-redux'
import {getOrderDetails, payOrder} from '../actions/orderAction'
import Message from '../components/Message'
import Loader from '../components/Loader'
import axios from 'axios'

const OrderScreen = () => {
    const {id} = useParams();
    const [sdkReady, setSdkReady] = useState(false)
    const orderId = id;
    const orderPay = useSelector(state => state.orderPay)
    const {loading:loadingPay, success:successPay} = orderPay
    const dispatch = useDispatch();
    const orderDetails = useSelector(state => state.orderDetails)
    const {order, loading,error} = orderDetails
    const addDecimal = (num) =>{
      return (Math.round(num*100)/100).toFixed(2)
    }
    //item's price
    let i = 0;
    order.orderItems.map((item)=> {
      i += (item.price * item.qty)
    })
    order.itemsPrice = addDecimal(i)

  const successPaymentHandler = (paymentResult) => {
      console.log(paymentResult)
      dispatch(payOrder(orderId,paymentResult))
  }

    useEffect(()=>{
      const addPaypalScript = async() =>{
        const {data: clientId} = await axios.get('/api/config/paypal')
        const script = document.createElement('script')
        script.type = 'text/javascript'
        script.src = `https://www.paypal.com/sdk/js?client-id=${clientId}`
        script.async = true
        console.log(sdkReady)
        script.onload = () => {
          setSdkReady(true)
          console.log(sdkReady)
          console.log('script on load')
        }
        document.body.appendChild(script)
      }
      if(!order || successPay){
        dispatch(getOrderDetails(orderId));
        dispatch({type:ORDER_PAY_RESET})
      }else if(!order.isPaid){
        if(!window.paypal){
          addPaypalScript()
        }else{
          setSdkReady(true)
          console.log(sdkReady)
        }
      }
    },[dispatch,orderId,order,successPay])


  return (
    loading ? <Loader /> : error ? <Message variant="danger">{error}</Message> :
    <>
        <h2>Order {order._id}</h2>
        <Row>
            <Col md={8}>
                <ListGroup.Item variant='flush'>
                    <h2>Shipping</h2>
                    <p><strong>Name : </strong>{order.user.name}</p>
                    <p><strong>Email : </strong>{order.user.email}</p>
                    <p>
                        <strong>Address : </strong>
                        {order.shippingAddress.address}, {order.shippingAddress.city}, {order.shippingAddress.postalcode}, {order.shippingAddress.country} 
                    </p>
                    {
                      order.isDeliverd ? (
                        <Message variant='success'>Deliverd on {order.paidAt}</Message>
                      ) : (<Message variant='danger'>Not Deliverd</Message>)
                    }
                </ListGroup.Item>
                <ListGroup.Item >
                    <h2>Payment Method</h2>
                    <p>
                        <strong>Method : </strong>
                        <strong>{order.paymentMethod}</strong>
                    </p>
                    {
                      order.isPaid ? <Message variant='success'>Paid On {order.paidAt}</Message> : <Message variant='danger'>Order Not Paid</Message>
                    }
                </ListGroup.Item>
                <ListGroup.Item>
                    <h2>Order Items</h2>
                    {/* {cartItems.length === 0 ? (<Message variant='danger'>Your Cart is Empty</Message>) :  */}
                    <ListGroup variant='flush'>
                      {order.orderItems.map((item, index) =>(
                        <ListGroup.Item key={index}>
                          <Row>
                            <Col md={2}>
                              <Image src={item.image} alt={item.name}fluid rounded></Image>
                            </Col>
                            <Col>
                            <Link to={`/product/${item.product}`}>{item.name}</Link>
                            </Col>
                            <Col md={4}>{item.qty} X ${item.price} = ${(item.price)*(item.qty)}</Col>
                          </Row>
                        </ListGroup.Item>
                      ))}
                    </ListGroup>
                </ListGroup.Item>

            </Col>
            <Col>

            <ListGroup.Item>
                <Row>
                  <Col>Items</Col>
                  <Col>${order.itemsPrice}</Col>
                </Row>
                <Row>
                  <Col>shipping</Col>
                  <Col>${order.shippingPrice}</Col>
                </Row>
                <Row>
                  <Col>Tax</Col>
                  <Col>${order.taxPrice}</Col>
                </Row>
                <Row>
                  <Col>Total</Col>
                  <Col>${order.totalPrice}</Col>
                </Row>
                </ListGroup.Item>

            {!order.isPaid && (<ListGroup.Item>
              {loadingPay && <Loader />}
              {!sdkReady ? (<Loader />) : (<PayPalButton  amount={order.totalPrice} onSuccess={successPaymentHandler}/>)}
            </ListGroup.Item>)}
            </Col>
        </Row>
    </>
  )
}

export default OrderScreen
