import axios from 'axios'
import { ORDER_CREATE_FAILS, ORDER_CREATE_REQUEST, ORDER_CREATE_SUCCESS, ORDER_DETAILS_FAILS, ORDER_DETAILS_REQUEST, ORDER_DETAILS_SUCCESS, ORDER_LIST_MY_FAILS, ORDER_LIST_MY_REQUEST, ORDER_LIST_MY_SUCCESS, ORDER_PAY_FAILS, ORDER_PAY_REQUEST, ORDER_PAY_SUCCESS } from '../constants/orderConstants'

export const createOrder = (order) => async(dispatch,getState) => {
    try {
        dispatch({
            type:ORDER_CREATE_REQUEST
        })
        const {userLogin:{userInfo}} = getState()
        const config = {headers: {'Content-Type' : 'application/json', Authorization:`Bearer ${userInfo.token}`}};
        const {data} = await axios.post('api/orders',order,config)
        dispatch({type:ORDER_CREATE_SUCCESS, payload: data})
    } catch (error) {
        dispatch({
            type: ORDER_CREATE_FAILS,
            payload : error.response && error.response.data.message ? error.response.data.message: error.message
        })
    }
}

export const getOrderDetails = (id) => async(dispatch,getState) => {
    try {
        dispatch({
            type:ORDER_DETAILS_REQUEST
        })
        const {userLogin:{userInfo}} = getState()
        const config = {headers: { Authorization:`Bearer ${userInfo.token}`}};
        const {data} = await axios.get(`/api/orders/${id}`,config)
        const userId = userInfo._id;
        dispatch({type:ORDER_DETAILS_SUCCESS, payload: data})
    } catch (error) {
        dispatch({
            type: ORDER_DETAILS_FAILS,
            payload : error.response && error.response.data.message ? error.response.data.message: error.message
        })
    }
}

export const payOrder = (orderId, paymentResult) => async(dispatch,getState) => {
    try {
        dispatch({
            type:ORDER_PAY_REQUEST
        });
        const {userLogin:{userInfo}} = getState()
        const config = {headers: { Authorization:`Bearer ${userInfo.token}`}};
        const {data} = await axios.get(`/api/orders/${orderId}/pay`,paymentResult,config)
        dispatch({type:ORDER_PAY_SUCCESS, payload: data})
    } catch (error) {
        dispatch({
            type: ORDER_PAY_FAILS,
            payload : error.response && error.response.data.message ? error.response.data.message: error.message
        })
    }
}

export const listOrder = (id) => async(dispatch,getState) =>{
    try {
        dispatch({
            type:ORDER_LIST_MY_REQUEST
        });
        const {userLogin:{userInfo}} = getState()
        const config = {headers: { Authorization:`Bearer ${userInfo.token}`}};
        const {data} = await axios.get(`/api/orders/myorders/${id}`,config)
        console.log(data)
        dispatch({type:ORDER_LIST_MY_SUCCESS, payload: data})
    } catch (error) {
        dispatch({
            type: ORDER_LIST_MY_FAILS,
            payload : error.response && error.response.data.message ? error.response.data.message: error.message
        })
    }
}
