import React,{useState,useEffect} from 'react'
import {Link , useLocation, useNavigate} from 'react-router-dom'
import {Form,Button,Row, Col, ListGroup, ListGroupItem} from 'react-bootstrap'
import {useDispatch,useSelector} from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import {getUserDetails, updateUserProfile} from '../actions/userAction'
import FormContainer from '../components/FormContainer'
import { listOrder } from '../actions/orderAction'

const ProfileScreen = () => {
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setconfirmPassword] = useState('')

  const [message , setMessage] = useState('')

  const history = useNavigate();
  const dispatch = useDispatch();
  const userDetails = useSelector(state => state.userDetails)
  const userLogin = useSelector(state=> state.userLogin)
  const {Loading,error,user} = userDetails
  const {userInfo} = userLogin




  const userUpdateProfile = useSelector(state => state.userUpdateProfile)
  const {success} = userUpdateProfile

  const orderList = useSelector(state => state.orderList)
  const {loading:loadingOrders, orders, error:errorOrders} = orderList

useEffect(() => {
    if(!userInfo){
        history('/login')
    }else{
        if(!user.name){
            dispatch(getUserDetails('profile'))

        }else{

            dispatch(listOrder(user._id))
            setName(user.name)
            setEmail(user.email)
        }
    }
},[history,userInfo,user,dispatch])



  const submitHandler =(e) => {

      e.preventDefault()
      if(password !== confirmPassword){
        setMessage("Password do not match")
      }else{
        setMessage(null)
        dispatch(updateUserProfile({id:user._id,name,email,password}))
      }



  }
  return (
    <>
        <Row>
          <Col md={4}>
          <h1>Update Information</h1>
          {error && <Message variant="danger">{error}</Message>}

          {Loading && <Loader />}
          {message && <Message variant="danger">{message}</Message> }
          { success && <Message variant="success">Profile Updated</Message>}
          <Form onSubmit={submitHandler}>
              <Form.Group controlId="name">
                  <Form.Label>Name</Form.Label>
                  <Form.Control type="text" placeholder="enter name" value={name}
                  onChange={(e) => setName(e.target.value)}></Form.Control>
              </Form.Group>
              <Form.Group controlId="email">
                  <Form.Label>Email Address</Form.Label>
                  <Form.Control type="email" placeholder="enter email" value={email}
                  onChange={(e) => setEmail(e.target.value)}></Form.Control>
              </Form.Group>
              <Form.Group controlId="password">
                  <Form.Label>Password</Form.Label>
                  <Form.Control type="password" placeholder="enter password" value={password}
                  onChange={(e) => setPassword(e.target.value)}></Form.Control>
              </Form.Group>
              <Form.Group controlId="confirmPassword">
                  <Form.Label>Confirm Password</Form.Label>
                  <Form.Control type="password" placeholder="re-enter password" value={confirmPassword}
                  onChange={(e) => setconfirmPassword(e.target.value)}></Form.Control>
              </Form.Group>
              <Form.Group>
                <Button type="submit" variant='primary' style={{marginTop: '20px', marginBottom: '10px'}}>Update</Button>
              </Form.Group>

          </Form>
          </Col>
          <Col md={8}>
                <h1>My Orders</h1>
                <ListGroup variant='flush'>
                      {orders.map((item, index) =>(
                        <ListGroup.Item key={index}>
                          <Row>

                            <Col>{item._id}</Col>
                            <Col md={4}></Col>
                          </Row>
                        </ListGroup.Item>
                      ))}
                    </ListGroup>
          </Col>
        </Row>    
    </>
  )
}

export default ProfileScreen
